import React, { useState, useRef, useEffect } from 'react';
import * as XLSX from 'xlsx';
// html2canvas and jsPDF are expected to be loaded via CDN in the HTML environment,
// so direct imports are removed to resolve compilation errors.
// They will be accessed as global variables (html2canvas, jsPDF).

// Type declarations for global variables
declare global {
    interface Window {
        html2canvas: any;
        jsPDF: any;
    }
}

// Define interfaces for our data structures
interface ActionItem {
    id: number;
    task: string;
    owner: string;
    dueDate: string;
}

interface MeetingMinute {
    id: number;
    title: string;
    date: string;
    time: string;
    attendees: string;
    agenda: string;
    discussion: string;
    actionItems: ActionItem[];
    nextMeeting: string;
}

interface Task {
    id: number;
    description: string;
    taskStartDate: string;
    taskEndDate: string;
    status: 'Not Started' | 'In Progress' | 'Completed' | 'Blocked';
    assignedTo: string;
}

interface ProjectSchedule {
    id: number;
    projectName: string;
    startDate: string;
    endDate: string;
    tasks: Task[];
}

interface TaskCard {
    id: number;
    title: string;
    description: string;
    assignedTo: string;
    priority: 'Low' | 'Medium' | 'High';
    status: 'Not Started' | 'In Progress' | 'Review' | 'Done';
    dueDate: string;
    tags: string[];
}

interface BudgetItem {
    id: number;
    category: string;
    description: string;
    estimatedCost: number;
    actualCost: number;
    earnedValue: number;
    notes: string;
}

interface ProjectBudget {
    id: number;
    projectName: string;
    totalBudget: number;
    items: BudgetItem[];
}

interface Risk {
    id: number;
    riskDescription: string;
    probability: 'Low' | 'Medium' | 'High';
    impact: 'Low' | 'Medium' | 'High';
    category: string;
    mitigation: string;
    owner: string;
    status: 'Open' | 'Mitigated' | 'Closed';
}

interface RiskRegister {
    id: number;
    projectName: string;
    risks: Risk[];
}

// Main App Component
const App = () => {
    // State to manage the currently active tab
    const [activeTab, setActiveTab] = useState('mom');
    // State to store Minutes of Meeting data
    const [momData, setMomData] = useState<MeetingMinute[]>([]);
    // State to store Timeline data
    const [projectScheduleData, setProjectScheduleData] = useState<ProjectSchedule[]>([]);
    // State to store Task Board data
    const [taskBoardData, setTaskBoardData] = useState<TaskCard[]>([]);
    // State to store Budget data
    const [budgetData, setBudgetData] = useState<ProjectBudget[]>([]);
    // State to store Risk Register data
    const [riskRegisterData, setRiskRegisterData] = useState<RiskRegister[]>([]);
